            signer.link<&{ZeedzINO.AdminClient}>(ZeedzINO.AdminClientPublicPath, target: ZeedzINO.AdminClientStoragePath)
        }

        self.clientCapability= signer.getCapability<&{ZeedzINO.AdminClient}>(ZeedzINO.AdminClientPublicPath)
